    String NomeAux, EnderecoAux, EmailAux, TelefoneAux, EstadoAux;
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        NomeAux = Nome.getText();
        EnderecoAux = Endereco.getText();
        EmailAux = Email.getText();
        TelefoneAux = Telefone.getText();
        Nome.setText("");
        Endereco.setText("");
        Email.setText("");
        Telefone.setText("");
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        Nome.setText(NomeAux);
        Endereco.setText(EnderecoAux);
        Email.setText(EmailAux);
        Telefone.setText(TelefoneAux);
    }//GEN-LAST:event_jButton2ActionPerformed

    private void UFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_UFActionPerformed
        EstadoAux = (String)UF.getSelectedItem();
    }//GEN-LAST:event_UFActionPerformed

    private void ImprimirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ImprimirActionPerformed
        System.out.println("Nome: " + Nome.getText());
        System.out.println("Endereco: " + Endereco.getText());
        System.out.println("Telefone: " + Telefone.getText());
        System.out.println("Email: " + Email.getText());
        System.out.println("UF: " + EstadoAux);
         
    }//GEN-LAST:event_ImprimirActionPerformed

